mapper.select:
SELECT COUNT(*) FROM MEMBER WHERE DEL_YN = 'N'
AND ID = #{keyword}
AND COMPANY_NM = #{keyword}

SELECT PRODUCT.PRODUCT_NM,SIZE.SIZE_IDX,SIZE.SIZE, SIZE.HEIGHT, SIZE.WIDTH, THICKNESS.THICKNESS, THICKNESS.THICKNESS_IDX, PRODUCT.PRODUCT_IDX, PRODUCT.PRODUCT_CD FROM PRODUCT_SIZE SIZE LEFT OUTER JOIN PRODUCT_THICKNESS THICKNESS ON SIZE.PRODUCT_IDX = THICKNESS.PRODUCT_IDX LEFT OUTER JOIN PRODUCT PRODUCT ON PRODUCT.PRODUCT_IDX = THICKNESS.PRODUCT_IDX WHERE PRODUCT.DEL_YN = 'N'
AND PRODUCT.PRODUCT_NM = #{keyword}
LIMIT #{i}, #{contentnum}

mapper.insert:
    INSERT INTO CLIENT_MONEY_INFO
	(
	company_nm
	)
	VALUES
	(
	#{companyNm}	
	)
	ON DUPLICATE KEY
	UPDATE
    company_nm = #{companyNm}

mapper.select:
		SELECT
		COUNT(*)
		FROM
		PRICE PRICE
		INNER JOIN PRODUCT PRODUCT ON PRODUCT.PRODUCT_IDX = PRICE.PRODUCT_IDX
		WHERE  
		PRODUCT.DEL_YN = 'N'
    	AND	PRICE.PRODUCT_IDX = #{productIdx}
    	AND	PRICE.THICKNESS = #{thickness}
    	AND	PRICE.SIZE = #{size}
    	AND	PRICE.ID = #{id}

mapper.update:
		UPDATE PRICE SET
		PRICE = #{price}, USED = #{use}
    	WHERE ID = #{id} AND product_idx = #{productIdx} AND thickness = #{thickness} AND size = #{size}

UPDATE 
	MEMBER 
SET 
	PASSWORD = #{password}
WHERE
	ID = #{id}
